class Circle {
    constructor(x, y, r) {
        this.x = x;
        this.y = y;
        this.r = r; 
    }

    draw(ctx) {
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.r, 0, Math.PI*2, true);
        ctx.closePath();
        ctx.fillStyle = 'yellow';
        ctx.fill();
    }
}

